func testPostCategoriesUpdate(t *testing.T) {
	t.Parallel()

	if 0 == len(postCategoryPrimaryKeyColumns) {
		t.Skip("Skipping table with no primary key columns")
	}
	if len(postCategoryAllColumns) == len(postCategoryPrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &PostCategory{}
	if err = randomize.Struct(seed, o, postCategoryDBTypes, true, postCategoryColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize PostCategory struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := PostCategories().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, postCategoryDBTypes, true, postCategoryPrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize PostCategory struct: %s", err)
	}

	if rowsAff, err := o.Update(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("should only affect one row but affected", rowsAff)
	}
}

func testPostCategoriesSliceUpdateAll(t *testing.T) {
	t.Parallel()

	if len(postCategoryAllColumns) == len(postCategoryPrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &PostCategory{}
	if err = randomize.Struct(seed, o, postCategoryDBTypes, true, postCategoryColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize PostCategory struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := PostCategories().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, postCategoryDBTypes, true, postCategoryPrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize PostCategory struct: %s", err)
	}

	// Remove Primary keys and unique columns from what we plan to update
	var fields []string
	if strmangle.StringSliceMatch(postCategoryAllColumns, postCategoryPrimaryKeyColumns) {
		fields = postCategoryAllColumns
	} else {
		fields = strmangle.SetComplement(
			postCategoryAllColumns,
			postCategoryPrimaryKeyColumns,
		)
	}

	value := reflect.Indirect(reflect.ValueOf(o))
	typ := reflect.TypeOf(o).Elem()
	n := typ.NumField()

	updateMap := M{}
	for _, col := range fields {
		for i := 0; i < n; i++ {
			f := typ.Field(i)
			if f.Tag.Get("boil") == col {
				updateMap[col] = value.Field(i).Interface()
			}
		}
	}

	slice := PostCategorySlice{o}
	if rowsAff, err := slice.UpdateAll(ctx, tx, updateMap); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("wanted one record updated but got", rowsAff)
	}
}
